INSERT INTO `contenido_pagina` (`id`, `nombre`, `archivo_vista`, `texto`, `pdf`, `imagen`, `estatus`, `pagina_cecyte_id`) VALUES	(1, 'Planes y programas', 'academica1.jpg', 'Realizar el análisis y la validación de los estudios de factibilidad y pertinencia relacionados con movimientos de creación, nueva operación, refrendo, actualización y liquidación de los programas educativos.', NULL, NULL, 1, 1),	(2, 'Interculturalidad', 'academica2.jpg', 'Dar seguimiento a la implementación del Programa Intercultural, con la finalidad de fomentar una cultura de inclusión en los 30 Colegios. Asimismo, realizar un primer encuentro nacional de Interculturalidad.', NULL, NULL, 1, 1),	(3, 'Oferta Educativa', 'academica3.jpg', 'Consistente en el proceso de registro de planteles y carreras ofertadas por los CECYTE a nivel nacional ante la D.G.P. y, con esos elementos, elaborar un concentrado global de lo registrado ante la instancia en comento.', NULL, NULL, 1, 1),	(4, 'Mesa de ayuda de la DGP', 'academica4.jpg', 'Asistir a las Direcciones Generales de los estados ante la mesa de ayuda de la D.G.P. principalmente en la transición a títulos electrónicos.', NULL, NULL, 1, 1),	(5, 'Guía Metodológica', 'academica5.jpg', 'Instrumento de apoyo para solicitar movimiento sobre la oferta educativa, tales como: refrendo, nueva carrera, nueva operación y liquidación.', NULL, NULL, 1, 1),	(6, 'Capacitación Docente', 'academica6.jpg', 'Se fomenta la difusión, participación y seguimiento de los cursos que ofrece la COSDAC; para mantener una capacitación continua de los docentes en los 30 Colegios.', NULL, NULL, 1, 1),	(7, 'Normalidad Mínima', 'academica7.jpg', 'Se aplica al cumplimiento de los rasgos mínimos de la normalidad escolar en los planteles, mediante una propuesta de materiales específicos que contribuyan a la mejora educativa.', NULL, NULL, 1, 1),	(8, 'Academias', 'academica8.jpg', 'Instrumento de apoyo para solicitar movimiento sobre la oferta educativa, tales como: refrendo, nueva carrera, nueva operación y liquidación.', NULL, NULL, 1, 1),	(9, 'EMSaD', 'academica9.jpg', 'Se fomenta la difusión, participación y seguimiento de los cursos que ofrece la COSDAC; para mantener una capacitación continua de los docentes en los 30 Colegios.\r\n\r\n', NULL, NULL, 1, 1),	(10, 'Cursos de Inducción', 'academica10.jpg', 'Se aplica al cumplimiento de los rasgos mínimos de la normalidad escolar en los planteles, mediante una propuesta de materiales específicos que contribuyan a la mejora educativa.\r\n\r\n', NULL, NULL, 1, 1),	(11, 'Profesionalización Docente', 'academica11.jpg', 'Las academias se llevan a cabo, con el propósito de conocer y socializar las metodologías empleadas por los docentes en cada campo disciplinar, así como identificar los problemas de aprendizaje de los alumnos para establecer las estrategias correspondientes.', NULL, NULL, 1, 1),	(12, 'Simplificación Administrativa', 'academica12.jpg', 'Se da seguimiento a la operación de los Centros EMSaD de acuerdo a la normatividad establecida en los 15 Colegios; así como orientar la aplicación de la misma.\r\n\r\n', NULL, NULL, 1, 1),	(13, 'Construyet', 'academica13.jpg', 'Se realiza con la finalidad de regularizar a los alumnos egresados de secundaria en Matemáticas, Lectura y Ciencias Experimentales, para su ingreso al nivel Medio Superior.\r\n\r\n', NULL, NULL, 1, 1),	(14, 'Normas de Control Escolar', 'academica14.jpg', 'Impulsar la capacitación y estadías docentes con otros centros e instituciones educativas nacionales e internacionales.\r\n\r\n', NULL, NULL, 1, 1),	(15, 'Imagen 1', 'vinculacion1.jpg', NULL, NULL, 'vinculacion1.jpg', 1, 2),	(16, 'Imagen 2', 'vinculacion2.jpg', NULL, NULL, 'vinculacion2.jpg', 1, 2),	(17, 'Imagen 3', 'vinculacion3.jpg', NULL, NULL, 'vinculacion3.jpg', 1, 2),	(18, 'Imagen 4', 'vinculacion4.jpg', NULL, NULL, 'vinculacion4.jpg', 1, 2),	(19, 'Imagen 5', 'vinculacion5.jpg', NULL, NULL, 'vinculacion5.jpg', 1, 2),	(20, 'Imagen 6', 'vinculacion6.jpg', NULL, NULL, 'vinculacion6.jpg', 1, 2),	(21, 'Imagen 7', 'vinculacion7.jpg', NULL, NULL, 'vinculacion7.jpg', 1, 2),	(22, 'Planeacion', 'planeacion1.jpg', NULL, 'planeacion.pdf', NULL, 1, 3),	(23, 'Guías Mecánicas', 'planeacion2.jpg', 'Es el programa mediante el cual se actualizan y/o elaboran proyectos ejecutivos de construcción de aulas, talleres, laboratorios y edificios administrativos que se requieren para la operación de los Colegios CECyTE.', NULL, NULL, 1, 3),	(24, 'Infraestructura Física Educativa', 'planeacion3.jpg', 'Con el objetivo de dar seguimiento a los Programas Anuales y así contar con la información correspondiente a la Infraestructura Física y/o Equipamiento, se les solicitara a los 30 Colegios la información cuantitativa, la cual está respaldada de los indicadores en el Proyecto No. 5 Fortalecimiento en Infraestructura y Equipamiento reportados en su Programa Anual 2020, en reportes trimestrales.', NULL, NULL, 1, 3),	(25, 'Diagnóstico de Infraestructura', 'planeacion4.jpg', 'Se les solicitara a los 30 Colegios el Historial y el Crecimiento del 2018 y 2019, en Infraestructura Física.\r\n\r\n', NULL, NULL, 1, 3),	(26, 'Revisión de Estudios de Factibilidad', 'planeacion5.jpg', 'Se recibirá la documentación correspondiente por parte de los Colegios, para cotejar los criterios y requisitos para la creación o conversión de instituciones de educación media superior, en la modalidad de organismos descentralizados locales (ODES), así como para la creación de planteles.', NULL, NULL, 1, 3),	(27, 'academica 1', 'cecyte.png', 'hola', NULL, NULL, 0, 1),	(28, 'asd', 'pikachu.PNG', NULL, NULL, 'pikachu.PNG', 0, 1),	(29, 'sdsd', 'ejemplo.png', NULL, 'VASCONI_.pdf', NULL, 0, 1),	(30, 'Banner Coordinación', 'banner.jpg', NULL, NULL, NULL, 1, 4),	(31, 'Politicas publica', 'LineasPoliticaPublica.png', NULL, 'LineasPoliticaPublica.pdf', NULL, 1, 5),	(32, 'Calendario 2020-2021', 'Calendario2020-2021.jpeg', NULL, 'Calendario2020-2021.pdf', NULL, 1, 5),	(33, 'Certificación Digital<br><br>\r\n', 'Recurso 1-100.jpg\r\n', 'Generación del módulo de certificación Digital para los alumnos de los 30 Colegios de Estudios Científicos y Tecnológicos.\r\n', NULL, NULL, 1, 7),	(34, 'Generación de Título Digital\r\n', 'Recurso 9-100.jpg\r\n', 'Generación del módulo de Título Digital para los alumnos de los 30 Colegios de Estudios Científicos y Tecnológicos.\r\n', NULL, NULL, 1, 7),	(35, 'Seguimiento de Egresados \r\n', 'Recurso 8-100.jpg\r\n', 'Implementar un sistema informático que permita la obtención de información respecto a los egresados de los CECyTEs.\r\n', NULL, NULL, 1, 7),	(36, '<br>Google for Education<br><br>', 'Recurso 10-100.jpg\r\n', 'Enriquecer el aprendizaje, utilizando herramientas digitales como apoyo a los alumnos y docentes fuera de las aulas.\r\n', NULL, NULL, 1, 7);
