INSERT INTO `pagina_cecyte` (`id`, `nombre`, `objetivo`, `carpeta`) VALUES	(1, 'Area Académica', 'El Área Académica tiene como objetivo coadyuvar, orientar, gestionar y acompañar a los ODES de los CECyTEs en los estados, mediante la elaboración, publicación y aplicación de reglamentos y manuales, facilitando el proceso de enseñanza aprendizaje en la formación integral y tecnológica de los estudiantes de bachillerato general y tecnológico en el marco del modelo de la Nueva Escuela Mexicana y en la profesionalización continua de directivos, docentes y personal de apoyo.\r\n\r\n', 'areas/academica/'),	(2, 'Área de Vinculación\r\n', NULL, 'areas/vinculacion/'),	(3, 'Área de Planeación y Espacio Educativos', 'Seguimiento y evaluación de metas y objetivos de la planeación institucional con base en las prioridades de la política educativa nacional, así como las propias formulada por la Coordinación. La estadística educativa, la gestión y seguimiento de programas federales de obras de infraestructura así como de su mantenimiento preventivo y correctivo, y en general, el desarrollo de insumos de información para una planeación eficaz y eficiente en la atención de las necesidades.', 'areas/planeacion/'),	(4, 'Inicio - Banners', NULL, 'inicio/banners/'),	(5, 'Inicio - Anuncios', NULL, 'inicio/anuncios/'),	(6, 'Inicio - Noticias', NULL, 'inicio/noticias/'),	(7, 'Área de Informática', 'asdsad', 'areas/informatica/');
